import React from "react";
import { Route, Redirect, useLocation } from "react-router-dom";
import { mapStateToPropsAuth } from "../../store/connectStore";
import { connect } from "react-redux";

const PrivateRoute = ({ state, ...props }) => {
  const isLogged = state;
  const location = useLocation();
  return isLogged.auth ? (
    <Route {...props} />
  ) : (
    <Redirect to={{ pathname: "/login", state: { from: location } }} />
  );
};

const wrapper = connect(mapStateToPropsAuth);
const HocPrivateRouter = wrapper(PrivateRoute);
export default HocPrivateRouter;
