import React from "react";
import useForm from "../../../hooks/useForm";
import { MailOutlined, LockOutlined } from "@ant-design/icons";
import { Input } from "antd";
import WrapperInput from "./Input";

const Form = (props) => {
  const { initialVaule } = props;

  const [form, handleChange] = useForm({
    initialVaule,
  });
  const { email, password } = form;

  const handleSubmit = (ev) => {
    ev.preventDefault();
    props.onSubmit(form);
  };

  console.log(props);
  return (
    <form onSubmit={handleSubmit} initialVaule={props.initialvalue}>
      <Input
        type={props.type}
        name="email"
        className={props.styles}
        prefix={<MailOutlined />}
        placeholder="Email"
        onChange={handleChange}
        value={email}
      />
      <Input
        type="password"
        name="password"
        className={props.styles}
        prefix={<LockOutlined />}
        placeholder="Password"
        onChange={handleChange}
        value={password}
      />

      {props.children}
    </form>
  );
};

export default Form;
