import NewAdvertForm from "./NewAdvertForm";

const NewAdvertPage = () => {
  const [state, setSatate] = useState({
    error: null,
  });
  return (
    <Layout title="New advert">
      <Divider>Create an advert</Divider>
      <NewAdvertForm onSubmit={this.handleSubmit} />
      {error && (
        <Alert
          afterClose={this.resetError}
          closable
          message={error}
          showIcon
          type="error"
        />
      )}
    </Layout>
  );
};

