const { createStore, combineReducers } = require("redux");

const AUTH_LOGIN = "auth/LOGIN";
const AUTH_LOGOUT = "auth/LOGOUT";
const ADVERTS_CREATE = "ads/CREATE";
const ADVERTS_DELETE = "ads/DELETE";
const ADVERTS_TAGS = "ads/TAGS";
const ADVERTS_ALL = "ads/ALL";

const defaultValue = {
  auth: false,
  adverts: { adverts: null },
  filters: { filters: null },
};

function auth(state = defaultValue, action) {
  switch (action.type) {
    case AUTH_LOGIN:
      return { ...state, ...action.payload };
    case AUTH_LOGOUT:
      return { ...state, ...action.payload };
    default:
      return state;
  }
}
function adverts(state = defaultValue, action) {
  switch (action.type) {
    case ADVERTS_CREATE:
      return { ...state, ...action.payload };
    case ADVERTS_DELETE:
      return { ...state, ...action.payload };
    case ADVERTS_TAGS:
      return { ...state, ...action.payload };
    case ADVERTS_ALL:
      return { ...state, adverts: action.payload };
    default:
      return state;
  }
}

function filters(state = defaultValue, action) {
  switch (action.type) {
    case ADVERTS_TAGS:
      return { ...state, ...action.payload };
    default:
      return state;
  }
}

const reducers = combineReducers({
  auth: auth,
  adverts: adverts,
  filters: filters,
});
export const store = createStore(reducers, defaultValue); // Creaos el store, pasandole un reducer y un estado

export function authLogin(payload) {
  return {
    type: AUTH_LOGIN,
    payload: { auth: payload },
  };
}

export function authLogout() {
  return {
    type: AUTH_LOGOUT,
    payload: { auth: false },
  };
}

export function allAdeverts(payload) {
  return {
    type: ADVERTS_ALL,
    payload: payload,
  };
}

export function delAdeverts(payload) {
  return {
    type: ADVERTS_DELETE,
    payload: payload,
  };
}

export function tagsAdeverts(payload) {
  return {
    type: ADVERTS_TAGS,
    payload: payload,
  };
}

export function newAdeverts(payload) {
  return {
    type: ADVERTS_CREATE,
    payload: payload,
  };
}
