import React from "react";
import { Link } from "react-router-dom";
import { Empty, Button, Spin, List, Divider } from "antd";
import { connect } from "react-redux";
import { mapStateToPropsAdverts } from "../../../store/connectStore";
import { allAdeverts, tagsAdeverts } from "../../../store/index";
import storage from "../../../utils/storage";
import { getAdverts, getTags } from "../../../api/adverts";
import Layout from "../../layout";
import FiltersForm from "./FiltersForm";
import AdvertCard from "./AdvertCard";

class AdvertsPage extends React.Component {
  state = {
    loading: false,
    error: null,
  };

  formatFilters = () => {
    const {
      filters: { name, sale, price, tags },
    } = this.props;

    const filters = {};
    if (name) {
      filters.name = name;
    }
    if (["sell", "buy"].includes(sale)) {
      filters.sale = sale === "sell";
    }
    if (price.length) {
      filters.price = price.join("-");
    }
    if (tags.length) {
      filters.tags = tags.join(",");
    }

    return filters;
  };

  getAdverts = () => {
    this.setState({ loading: true, error: null });
    getAdverts()
      .then(({ result }) => {
        this.setState({ loading: false, error: null });
        this.props.allAdeverts(result.rows);
      })
      .catch((err) => {
        this.setState({ loading: false, error: err });
      });
  };

  handleSubmit = (filters) => {
    storage.set("filters", filters);
    this.setState({ filters });
  };

  renderLoading = () => (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <Spin size="large" />
    </div>
  );

  renderError = () => {
    const { error } = this.state;
    return (
      <Empty
        description={<span style={{ color: "#ff4d4f" }}>{`${error}`}</span>}
      >
        <Button type="primary" danger onClick={this.getAdverts}>
          Reload
        </Button>
      </Empty>
    );
  };

  renderEmpty = () => {
    const { filters } = this.props;
    const isFiltered = filters;

    return (
      <Empty description={<span>No adverts here!</span>}>
        {isFiltered ? (
          <span>Refine your search</span>
        ) : (
          <Link to="/adverts/new">
            <Button type="primary">Create the first one</Button>
          </Link>
        )}
      </Empty>
    );
  };

  renderAdvert = (advert) => {
    return (
      <List.Item>
        <Link to={`/adverts/${advert._id}`}>
          <AdvertCard {...advert} />
        </Link>
      </List.Item>
    );
  };

  renderAdverts = () => {
    const { loading, error } = this.state;
    const { adverts } = this.props.adverts;
    if (loading) {
      return this.renderLoading();
    }
    if (error) {
      return this.renderError();
    }

    if (!adverts) {
      return null;
    }

    if (!adverts) {
      return this.renderEmpty();
    }

    return (
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={adverts}
        renderItem={this.renderAdvert}
      />
    );
  };
  componentDidMount() {
    this.getAdverts();
    getTags().then((tags) => {
      this.props.tagsAdeverts(tags);
    });
  }

  componentDidUpdate(prevProps, { filters: prevFilters }) {
    const { filters } = this.props;
    if (filters !== prevFilters) {
      this.getAdverts();
    }
  }

  render() {
    console.log(this.props);
    const { filters } = this.props;
    return (
      <Layout title="Adverts list">
        <Divider>Filter your adverts</Divider>
        <FiltersForm initialFilters={filters} onSubmit={this.handleSubmit} />
        <Divider>Adverts</Divider>
        {this.renderAdverts()}
      </Layout>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    allAdeverts: (payload) => dispatch(allAdeverts(payload)),
    tagsAdeverts: (payload) => dispatch(tagsAdeverts(payload)),
  };
};
export default connect(mapStateToPropsAdverts, mapDispatchToProps)(AdvertsPage);
