const { createStore } = require("redux");

const AUTH_LOGIN = "auth/LOGIN";
const AUTH_LOGOUT = "auth/LOGOUT";
const ADVERTS_CREATE = "ads/CREATE";
const ADVERTS_DELETE = "ads/DELETE";
const ADVERTS_TAGS = "ads/TAGS";
const ADVERTS_ALL = "ads/ALL";

function auth(state, action) {
  switch (action.type) {
    case AUTH_LOGIN:
      return action.payload;
    case AUTH_LOGOUT:
      return action.payload;
    default:
      return state;
  }
}
function adverts(state, action) {
  switch (action.type) {
    case AUTH_LOGIN:
      return action.payload;
    case AUTH_LOGOUT:
      return action.payload;
    default:
      return state;
  }
}
const defaultValue = {
  auth: false,
};

export const store = createStore(auth, defaultValue); // Creaos el store, pasandole un reducer y un estado

export function authLogin(payload) {
  return {
    type: AUTH_LOGIN,
    payload: { auth: payload },
  };
}

export function authLogout() {
  return {
    type: AUTH_LOGOUT,
    payload: { auth: false },
  };
}

export function allAdeverts(payload) {
  return {
    type: ADVERTS_ALL,
    payload: payload,
  };
}

export function delAdeverts(payload) {
  return {
    type: ADVERTS_DELETE,
    payload: payload,
  };
}

export function tagsAdeverts(payload) {
  return {
    type: ADVERTS_TAGS,
    payload: payload,
  };
}

export function newAdeverts(payload) {
  return {
    type: ADVERTS_CREATE,
    payload: payload,
  };
}
